import React from 'react';

class AddImage extends React.Component {
    state = {
        title: "",
        image: {},
        description: ""
    }

    add = (e) => {
        e.preventDefault();
        console.log(this.state);
        if(this.state.title === "" || this.state.description === "") {
            alert("All fields need to be fill..");
            return;
        }
        const file = e.target.files;
        console.log(file);
        this.props.addImageHandler(this.state);
        this.setState({title:"", image: {}, description:""});
        this.props.history.push("/");
    }

    render() {
        const [selectedFile, setSelectedFile] = useState();
        const [isFilePicked, setIsFilePicked] = useState(false);

        const changeHandler = (event) => {
            setSelectedFile(event.target.files[0]);
            setIsSelected(true);
        };

        const handleSubmission = () => {
            const formData = new FormData();

            formData.append('File', selectedFile);


        };

        return (
            <div className="ui main add-contact">
                <div className="ui container">
                    <div className="ui header">Add Image</div>
                    <form className="ui form" onSubmit={this.add}>
                        <div className="field">
                            <label>Title</label>
                            <input type="text"
                                   name="title"
                                   placeholder="Title"
                                   value={ this.state.title }
                                   onChange={ (e) => this.setState({title: e.target.value}) } />
                        </div>
                        <div className="field">
                            <label>Image</label>
                            <input type="file"
                                   name="image"
                                   onChange={changeHandler}/>
                        </div>
                        <div className="field">
                            <label>Description</label>
                            <textarea name="description"
                                      cols="30"
                                      rows="10"
                                      onChange={ (e) => this.setState({description: e.target.value}) } >
                                { this.state.description }
                            </textarea>
                        </div>
                        <button className="ui button" type="submit"  onClick={handleSubmission}>Add</button>
                    </form>
                </div>
            </div>
        );
    }
}

export default AddImage;
