import React, { useEffect, useState } from 'react';

function AddImage() {
    this.state = {
        title : "",
        description: "",
        image: ""
    }
    const [selectedFile, setSelectedFile] = useState({});
    const [isFilePicked, setIsFilePicked] = useState(false);

    const changeHandler = (event) => {
        setSelectedFile(event.target.files[0]);
        setIsFilePicked(true);
    };

    const handleSubmission = () => {
        const formData = new FormData();
        formData.append('File', selectedFile);

        this.props.addImageHandler(formData);
        this.setState({title:"", image: {}, description:""});
        this.props.history.push("/");
    };

    return (
        <div className="ui main add-contact">
            <div className="ui container">
                <div className="ui header">Add Image</div>
                <form className="ui form">
                    <div className="field">
                        <label>Title</label>
                        <input type="text"
                               name="title"
                               placeholder="Title"
                               value={ this.state.title }
                               onChange={ (e) => this.setState({title: e.target.value}) } />
                    </div>
                    <div className="field">
                        <label>Image</label>
                        <input type="file"
                               name="image"
                               onChange={changeHandler}/>
                    </div>
                    <div className="field">
                        <label>Description</label>
                        <textarea name="description"
                                  cols="30"
                                  rows="10"
                                  onChange={ (e) => this.setState({description: e.target.value}) } >
                            { this.state.description }
                        </textarea>
                    </div>
                    <button className="ui button" type="submit" onClick={handleSubmission}>Add</button>
                </form>
            </div>
        </div>
    );
}

export default AddImage;
