import React from 'react';

class AddImage extends React.Component {
    state = {
        title: "",
        image: null,
        description: ""
    }

    changeHandler = (event) => {
        console.log(event.target.files[0]);
        this.setState({image:event.target.files[0]});
    };

    handleSubmission = (event) => {
        event.preventDefault();
        console.log(event);
        const formData = new FormData();
        if(this.state.title === "" || this.state.description === "") {
            alert("All fields need to be fill..");
            return;
        }
        formData.append("image", event.target.files[0]);
        formData.append("title", "StringValueTest");
        formData.append("description", "StringValueTest");
        console.log(formData);
        this.props.addImageHandler(this.state);
        this.setState({title:"", image: {}, description:""});
        this.props.history.push("/");
    };

    render() {

        return (
            <div className="ui main add-contact">
                <div className="ui container">
                    <div className="ui header">Add Image</div>
                    <form className="ui form" onSubmit={this.handleSubmission}>
                        <div className="field">
                            <label>Title</label>
                            <input type="text"
                                   name="title"
                                   placeholder="Title"
                                   value={ this.state.title }
                                   onChange={ (e) => this.setState({title: e.target.value}) } />
                        </div>
                        <div className="field">
                            <label>Image</label>
                            <input type="file"
                                   name="image"
                                   onChange={this.changeHandler}/>
                        </div>
                        <div className="field">
                            <label>Description</label>
                            <textarea name="description"
                                      cols="30"
                                      rows="10"
                                      onChange={ (e) => this.setState({description: e.target.value}) } >
                                { this.state.description }
                            </textarea>
                        </div>
                        <button className="ui button" type="submit" >Add</button>
                    </form>
                </div>
            </div>
        );
    }
}

export default AddImage;
