import React from 'react';

class EditImage extends React.Component {
    state = {
        title: "",
        image: {},
        description: ""
    }

    add = (e) => {
        e.preventDefault();

        if(this.state.title === "" || this.state.description === "") {
            alert("All fields need to be fill..");
            return;
        }
        this.props.addImageHandler(this.state);
        this.setState({title:"", image: {}, description:""});
        this.props.history.push("/");
    }

    render() {
        const {title, description, image} = this.props.location.state.image;
        return (
            <div className="ui main edit-contact">
                <div className="ui container">
                    <div className="ui header">Edit Image - []</div>
                    <form className="ui form" onSubmit={this.add}>
                        <div className="field">
                            <label>Title</label>
                            <input type="text"
                                   name="title"
                                   placeholder="Title"
                                   value={ this.state.title }
                                   onChange={ (e) => this.setState({title: e.target.value}) } />
                        </div>
                        <div className="field">
                            <label>Image</label>
                            <input type="file"
                                   name="image"/>
                        </div>
                        <div className="field">
                            <img className="image" src={ this.state.image } alt=""/>
                        </div>
                        <div className="field">
                            <label>Description</label>
                            <textarea name="description"
                                      cols="30"
                                      rows="10"
                                      onChange={ (e) => this.setState({description: e.target.value}) } >
                                { this.state.description }
                            </textarea>
                        </div>
                        <button className="ui button" type="submit">Add</button>
                    </form>
                </div>
            </div>
        );
    }
}

export default EditImage;
