import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { uuid } from "uuidv4";
import Header from "./components/Header";
import AddImage from "./components/AddImage";
import EditImage from "./components/EditImage";
import ImageList from "./components/ImageList";

function App() {
  const [images, setImages] = useState([]);
  const LOCAL_STORAGE_KEY = "images";

  const addImageHandler = (image) => {
    setImages([...images, { id: uuid(), ...image }]);
  }

  const editImageHandler = (image) => {
    setImages([...images, { id: uuid(), ...image }]);
  }

  const removeImageHandler = (id) => {
    const filteredContacts = images.filter(function (contact) {
      return contact.id !== id;
    })

    setImages(filteredContacts);
  }

  useEffect(() => {
    const retriveImages = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY));
    if(retriveImages)
      setImages(retriveImages);
  }, []);

  useEffect(() => {
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(images))
  }, [images]);

  return (
      <div className="pusher">

        <Router>
          <Header/>
          <Switch>
            <Route path="/" exact
                   render={(props) => (
                       <ImageList
                           {...props}
                           images={images}
                           getImageID={removeImageHandler}/>
                   )}/>
            <Route path="/add" exact
                   render={(props) => (
                       <AddImage
                           {...props}
                           addImageHandler={addImageHandler}/>
                   )}/>
            <Route path="/image/:id" exact
                   render={(props) => (
                       <EditImage
                           {...props}
                           editImageHandler={editImageHandler}/>
                   )}/>
          </Switch>

        </Router>

      </div>
  );
}

export default App;
