import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { uuid } from "uuidv4";
import api from "./api/Setup";
import Header from "./components/Header";
import AddImage from "./components/AddImage";
import EditImage from "./components/EditImage";
import ImageList from "./components/ImageList";

function App() {
  const [images, setImages] = useState([]);
  const [pagination, setPagination] = useState([]);
  const [page, setPage] = useState(1);
  const LOCAL_STORAGE_KEY = "images";

  const retriveImages = async () => {
    const response = await api.get('api/images', { params: { page: page }});

    if(response.data.success == true) {
      return response.data.data;
    }
    return;
  }

  const addImageHandler = async (image) => {
    console.log(image);
    const response = await api.post('api/images',
        image,
        {
          headers: {
            "Content-type": "multipart/form-data",
            'Access-Control-Allow-Origin': 'http://localhost:3000',
            'Access-Control-Allow-Credentials': 'true'
          },
        });
    console.log(response);
    if(response.data.success == true) {
      return response.data.data;
    }
    return;
    setImages([...images, { id: uuid(), ...image }]);
  }

  const editImageHandler = (image) => {
    setImages([...images, { id: uuid(), ...image }]);
  }

  const removeImageHandler = (id) => {
    const filteredContacts = images.filter(function (contact) {
      return contact.id !== id;
    })

    setImages(filteredContacts);
  }

  useEffect(() => {
    /*const retriveImages = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY));
    if(retriveImages)
      setImages(retriveImages);*/

    const getAllImages = async () => {
      const allImages = await retriveImages();

      if(allImages.data) {
        setImages(allImages.data);
        console.log(123);
        setPagination(allImages);
      }
      console.log(pagination);
    }

    getAllImages();
  }, []);

  useEffect(() => {
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(images))
  }, [images]);

  return (
      <div className="pusher">

        <Router>
          <Header/>
          <Switch>
            <Route path="/" exact
                   render={(props) => (
                       <ImageList
                           {...props}
                           images={images}
                           pagination={pagination}
                           getImageID={removeImageHandler}/>
                   )}/>
            <Route path="/add" exact
                   render={(props) => (
                       <AddImage
                           {...props}
                           addImageHandler={addImageHandler}/>
                   )}/>
            <Route path="/image/:id" exact
                   render={(props) => (
                       <EditImage
                           {...props}
                           editImageHandler={editImageHandler}/>
                   )}/>
          </Switch>

        </Router>

      </div>
  );
}

export default App;
